import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Form, Button, Table, Card, InputGroup, FormControl, Alert } from 'react-bootstrap';

const Lectures = ({ role }) => {
  const [courses, setCourses] = useState([]);
  const [formData, setFormData] = useState({ course_id: '', lecturer_id: '' });
  const [search, setSearch] = useState('');
  const [message, setMessage] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchCourses();
  }, []);

  const fetchCourses = async (q = '') => {
    try {
      const res = await axios.get(
        `${process.env.REACT_APP_API_URL}/courses${q ? `?q=${q}` : ''}`,
        { headers: { 'x-auth-token': localStorage.getItem('token') } }
      );
      setCourses(res.data);
    } catch (err) {
      console.error(err);
      setError('Failed to fetch courses');
    }
  };

  const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    setMessage(null);
    setError(null);

    try {
      const res = await axios.post(
        `${process.env.REACT_APP_API_URL}/lectures`,
        { course_id: Number(formData.course_id), lecturer_id: Number(formData.lecturer_id) },
        { headers: { 'x-auth-token': localStorage.getItem('token') } }
      );
      setMessage(`Lecture assigned successfully: ID ${res.data.lecture_id}`);
      setFormData({ course_id: '', lecturer_id: '' });
      fetchCourses();
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.message || 'Failed to assign lecture');
    }
  };

  const handleSearch = e => {
    setSearch(e.target.value);
    fetchCourses(e.target.value);
  };

  return (
    <Card className="p-4">
      {role === 'pl' && (
        <Form onSubmit={onSubmit} className="mb-4">
          <h5>Assign Lecturer to Course</h5>
          {message && <Alert variant="success">{message}</Alert>}
          {error && <Alert variant="danger">{error}</Alert>}

          <Form.Group controlId="course_id" className="mb-3">
            <Form.Label>Course ID</Form.Label>
            <Form.Control type="number" name="course_id" value={formData.course_id} onChange={onChange} required />
          </Form.Group>

          <Form.Group controlId="lecturer_id" className="mb-3">
            <Form.Label>Lecturer ID</Form.Label>
            <Form.Control type="number" name="lecturer_id" value={formData.lecturer_id} onChange={onChange} required />
          </Form.Group>

          <Button variant="primary" type="submit">Assign Lecture</Button>
        </Form>
      )}

      <InputGroup className="mb-3">
        <FormControl placeholder="Search courses..." value={search} onChange={handleSearch} />
      </InputGroup>

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Course Name</th>
            <th>Course Code</th>
            <th>Lecture ID</th>
          </tr>
        </thead>
        <tbody>
          {courses.map(course => (
            <tr key={course.id}>
              <td>{course.id}</td>
              <td>{course.name}</td>
              <td>{course.code}</td>
              <td>{course.lecturer_id || 'N/A'}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Card>
  );
};

export default Lectures;
