// src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';
import jwtDecode from 'jwt-decode';
import 'bootstrap/dist/css/bootstrap.min.css';
import './index.css';

import Navbar from './components/Navbar';
import AuthPage from './pages/AuthPage';
import Reports from './components/Reports';
import Courses from './components/Courses';
import Classes from './components/Classes';
import Monitoring from './components/Monitoring';
import Rating from './components/Rating';
import Lectures from './components/Lectures';

const App = () => {
  const [role, setRole] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decoded = jwtDecode(token);
        setRole(decoded.role);
        axios.defaults.headers.common['x-auth-token'] = token;
      } catch (err) {
        console.error('Invalid token', err);
        localStorage.removeItem('token');
        setRole(null);
      }
    }
  }, []);

  const logout = () => {
    localStorage.removeItem('token');
    setRole(null);
  };

  return (
    <Router>
      {role && <Navbar role={role} logout={logout} />}
      <div className="container mt-4">
        <Routes>
          <Route path="/" element={role ? <Navigate to="/reports" /> : <AuthPage setRole={setRole} />} />
          <Route path="/login" element={<Navigate to="/" replace />} />
          <Route path="/register" element={<Navigate to="/" replace />} />
          <Route path="/reports" element={role ? <Reports role={role} /> : <Navigate to="/" />} />
          <Route path="/courses" element={role ? <Courses role={role} /> : <Navigate to="/" />} />
          <Route path="/classes" element={role ? <Classes role={role} /> : <Navigate to="/" />} />
          <Route path="/monitoring" element={role ? <Monitoring role={role} /> : <Navigate to="/" />} />
          <Route path="/rating" element={role ? <Rating role={role} /> : <Navigate to="/" />} />
          <Route path="/lectures" element={role ? <Lectures role={role} /> : <Navigate to="/" />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
