import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Button, Form, Alert } from 'react-bootstrap';

const Rating = () => {
  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

  const [lectures, setLectures] = useState([]);
  const [students, setStudents] = useState([]);
  const [ratings, setRatings] = useState([]);
  const [selectedId, setSelectedId] = useState('');
  const [rating, setRating] = useState('');
  const [comment, setComment] = useState('');
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role');

  // ✅ Fetch items based on user role
  useEffect(() => {
    const fetchItems = async () => {
      try {
        if (role === 'student') {
          const { data } = await axios.get(`${API_URL}/rating/lectures-to-rate`, {
            headers: { Authorization: `Bearer ${token}` },
          });
          setLectures(data);
        } else if (role === 'pl') {
          const { data } = await axios.get(`${API_URL}/rating/students-to-rate`, {
            headers: { Authorization: `Bearer ${token}` },
          });
          setStudents(data);
        }
      } catch (err) {
        console.error('Fetch items error:', err);
        setError('Failed to fetch items to rate.');
      }
    };
    fetchItems();
  }, [API_URL, role, token]);

  // ✅ Fetch all ratings
  useEffect(() => {
    const fetchRatings = async () => {
      try {
        const { data } = await axios.get(`${API_URL}/rating`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setRatings(data);
      } catch (err) {
        console.error('Fetch ratings error:', err);
        setError('Failed to fetch ratings.');
      }
    };
    fetchRatings();
  }, [API_URL, token]);

  // ✅ Handle submit
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!selectedId || !rating) {
      setError('Please select an item and provide a rating.');
      return;
    }

    try {
      await axios.post(
        `${API_URL}/rating`,
        { selectedId, rating, comment },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setSuccess('Rating submitted successfully!');
      setError(null);
      setSelectedId('');
      setRating('');
      setComment('');
    } catch (err) {
      console.error('Submit rating error:', err);
      setError('Failed to submit rating.');
    }
  };

  return (
    <div className="container mt-4">
      <Card className="p-4 shadow-sm">
        <h3 className="mb-3 text-center">Rating System</h3>

        {error && <Alert variant="danger">{error}</Alert>}
        {success && <Alert variant="success">{success}</Alert>}

        <Form onSubmit={handleSubmit}>
          {role === 'student' && (
            <Form.Group className="mb-3">
              <Form.Label>Select Lecture</Form.Label>
              <Form.Select
                value={selectedId}
                onChange={(e) => setSelectedId(e.target.value)}
              >
                <option value="">-- Select Lecture --</option>
                {lectures.map((lec) => (
                  <option key={lec.id} value={lec.id}>
                    {lec.lecture_name}
                  </option>
                ))}
              </Form.Select>
            </Form.Group>
          )}

          {role === 'pl' && (
            <Form.Group className="mb-3">
              <Form.Label>Select Student</Form.Label>
              <Form.Select
                value={selectedId}
                onChange={(e) => setSelectedId(e.target.value)}
              >
                <option value="">-- Select Student --</option>
                {students.map((stu) => (
                  <option key={stu.id} value={stu.id}>
                    {stu.username}
                  </option>
                ))}
              </Form.Select>
            </Form.Group>
          )}

          <Form.Group className="mb-3">
            <Form.Label>Rating (1–5)</Form.Label>
            <Form.Control
              type="number"
              min="1"
              max="5"
              value={rating}
              onChange={(e) => setRating(e.target.value)}
              placeholder="Enter rating"
            />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Comment</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              placeholder="Add a comment (optional)"
            />
          </Form.Group>

          <Button type="submit" variant="primary" className="w-100">
            Submit Rating
          </Button>
        </Form>

        <hr />

        <h5 className="mt-4">Existing Ratings</h5>
        {ratings.length > 0 ? (
          ratings.map((r, idx) => (
            <Card key={idx} className="mt-2 p-2">
              <strong>Rating:</strong> {r.rating} ⭐
              <br />
              <strong>Comment:</strong> {r.comment || 'No comment'}
            </Card>
          ))
        ) : (
          <p>No ratings yet.</p>
        )}
      </Card>
    </div>
  );
};

export default Rating;
