import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Rating = ({ role }) => {
  const [form, setForm] = useState({
    lecture_id: '',
    target_id: '', // student_id if lecturer, lecturer_id if student
    rating: '',
    comment: ''
  });
  const [message, setMessage] = useState('');
  const [lectures, setLectures] = useState([]);
  const [targets, setTargets] = useState([]);
  const [existingRatings, setExistingRatings] = useState([]);

  // Fetch lectures and target users
  useEffect(() => {
    const fetchLectures = async () => {
      try {
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/lectures`, {
          headers: { 'x-auth-token': localStorage.getItem('token') }
        });
        setLectures(res.data);
      } catch (err) {
        console.error('Error fetching lectures:', err);
      }
    };

    const fetchTargets = async () => {
      try {
        const type = role === 'student' ? 'lecturer' : 'student';
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/auth/users?role=${type}`, {
          headers: { 'x-auth-token': localStorage.getItem('token') }
        });
        setTargets(res.data);
      } catch (err) {
        console.error('Error fetching targets:', err);
      }
    };

    fetchLectures();
    fetchTargets();
  }, [role]);

  // Fetch existing ratings for selected lecture or target
  useEffect(() => {
    const fetchRatings = async () => {
      if (!form.lecture_id && !form.target_id) return;

      try {
        const endpoint = role === 'student' 
          ? `/rating/lecture/${form.lecture_id}` 
          : `/rating/student/${form.target_id}`;
        const res = await axios.get(`${process.env.REACT_APP_API_URL}${endpoint}`, {
          headers: { 'x-auth-token': localStorage.getItem('token') }
        });
        setExistingRatings(res.data);
      } catch (err) {
        console.error('Error fetching ratings:', err);
      }
    };

    fetchRatings();
  }, [form.lecture_id, form.target_id, role]);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(
        `${process.env.REACT_APP_API_URL}/rating`,
        form,
        { headers: { 'x-auth-token': localStorage.getItem('token') } }
      );
      setMessage(res.data.message);
      setForm({ lecture_id: '', target_id: '', rating: '', comment: '' });
      setExistingRatings([...existingRatings, res.data]); // show new rating
    } catch (err) {
      setMessage(err.response?.data?.message || 'Error submitting rating');
      console.error('Rating submit error:', err);
    }
  };

  return (
    <div>
      <h2>{role === 'student' ? 'Rate Lecturer' : 'Rate Student'}</h2>
      {message && <p>{message}</p>}
      <form onSubmit={handleSubmit}>
        <select name="lecture_id" value={form.lecture_id} onChange={handleChange} required>
          <option value="">Select Lecture</option>
          {lectures.map((lec) => (
            <option key={lec.id} value={lec.id}>{lec.course_name || `Lecture ${lec.id}`}</option>
          ))}
        </select>

        <select name="target_id" value={form.target_id} onChange={handleChange} required>
          <option value="">{role === 'student' ? 'Select Lecturer' : 'Select Student'}</option>
          {targets.map((t) => (
            <option key={t.id} value={t.id}>{t.username}</option>
          ))}
        </select>

        <input
          type="number"
          name="rating"
          placeholder="Rating (1-5)"
          min="1"
          max="5"
          value={form.rating}
          onChange={handleChange}
          required
        />
        <textarea
          name="comment"
          placeholder="Comment (optional)"
          value={form.comment}
          onChange={handleChange}
        />
        <button type="submit">Submit Rating</button>
      </form>

      {existingRatings.length > 0 && (
        <div>
          <h3>Existing Ratings</h3>
          <ul>
            {existingRatings.map((r) => (
              <li key={r.id}>
                {role === 'student' 
                  ? `Student ${r.student_id} rated: ${r.rating} - ${r.comment || 'No comment'}`
                  : `Lecture ${r.lecture_id} rated: ${r.rating} - ${r.comment || 'No comment'}`
                }
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Rating;
